/*
数据库： library_db;
表：
	用户表  user_table
    书籍表  book_table
*/


-- 1.book_table 创建
DROP DATABASE IF EXISTS library_db;
CREATE DATABASE library_db;
use library_db;
DROP TABLE IF EXISTS book;
SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

CREATE TABLE book(
	`book_id` int UNSIGNED NOT NULL COMMENT '书的id',
	`book_name` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '书名',
	`author` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '作者',
	`publishing_house` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '出版社',
	`year_of_publication` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '出版时间',
	`pages` int NULL DEFAULT NULL COMMENT '页数',
	`translator` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '译者',
	`ISBN` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'ISBN号码',
	`price` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '书的价钱',
	`score` float NULL DEFAULT NULL COMMENT '书的评分',
	`label` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '分类标签',
	PRIMARY KEY (`book_id`) USING BTREE
)ENGINE = MyISAM AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


SET FOREIGN_KEY_CHECKS = 1;
use library_db;
ALTER TABLE book RENAME TO book_table;
-- end of book_table


-- 2.user_table 创建
use library_db;
DROP TABLE IF EXISTS user_table;

CREATE TABLE user_table(
	`user_id` INT UNSIGNED NOT NULL COMMENT '用户id',
    `user_password` VARCHAR(32) NOT NULL COMMENT '用户密码',
	`user_phone` BIGINT UNSIGNED NULL COMMENT '用户手机号码',
    `faculty_id` INTEGER UNSIGNED NULL COMMENT '读者学号or工号',
    `reader_state` TINYINT NOT NULL DEFAULT 0 COMMENT '读者身份状态',
    `reader_name` VARCHAR (32) NULL COMMENT '读者昵称',
	PRIMARY KEY (`user_id`)
)

-- end of user_table


-- 给book_table添加新字段bstate，表示剩余量，默认为1
use library_db;
ALTER TABLE book_table ADD bstate INT DEFAULT 1;

